function allowDrop(e){e.preventDefault()}function showAdd(e){e.preventDefault(),$(e.target).parent().children(".add").slideDown()}function hideAdd(e){e.preventDefault(),$(e.target).parent().children(".add").slideUp()}function drag(e){e.dataTransfer.setData("tsm_id",e.target.id),$(e.target).parent().children(".delete").slideDown()}function hideAll(e){e.preventDefault(),$(".add").slideUp(),$(".delete").slideUp()}function deleteMember(e){e.preventDefault();var t=e.dataTransfer.getData("tsm_id");alert("deleteMember "+t.toString()),$.ajax({url:"/tabling_slot_members/"+t,type:"DELETE"}).done(function(){location.reload()}).fail(function(){alert("failed")})}function addMember(e){e.preventDefault();var t=e.dataTransfer.getData("tsm_id"),a=$(e.target).parent().parent().attr("id");console.log(location.hostname),alert("addMember "+t.toString()+" "+a.toString()),$.ajax({url:"/tabling_slot_members/"+t,type:"PUT",data:{tabling_slot_id:a}}).done(function(){location.reload()}).fail(function(){alert("failed")})}$(document).ready(function(){$(".add").slideUp(),$(".delete").slideUp()});